export function Icon({
  name,
  size = 36,
  color = '#232323',
  strokeWidth = 1.5,
  className,
  onPress,
  disabled = false,
}: IconProps) {
  const iconElement = (
    <Svg width={size} height={size} viewBox="0 0 36 36" className={className}>
      <G stroke={color} strokeWidth={strokeWidth}>
        {ICONS[name]}
      </G>
    </Svg>
  );

  if (onPress) {
    return (
      <TouchableOpacity
        onPress={onPress}
        disabled={disabled}
        className={[
          disabled ? 'opacity-50' : 'pressed:opacity-70',
          'items-center justify-center',
        ]
          .filter(Boolean)
          .join(' ')}
      >
        {iconElement}
      </TouchableOpacity>
    );
  }

  return iconElement;
}
